import { IConnectArgs, IContext } from '../../@types'
import withAuth from '../decorators/withAuth'

export const networkMutation = () => ({
  connect: withAuth(async (_parent: unknown, args: IConnectArgs, { wifi }: IContext): Promise<boolean> => {
    await wifi.connect(args)
    return true
  }),
  disconnect: withAuth(async (_parent: unknown, _args: unknown, { wifi }: IContext): Promise<boolean> => {
    return wifi.disconnect()
  })
})
